$c-set-bg: #fff;
$c-set-txt: #000;

:root {
  --c-bg: #{$c-set-bg};
  --c-txt: #{$c-set-txt};
}

$c-bg: var(--c-bg);
$c-txt: var(--c-txt);

@import '../utils';

* {
  box-sizing: border-box;
  font-family: 'Grtsk Variable', Helvetica, sans-serif;
  font-weight: 500;
}

body {
  margin: 0;
  overflow-x: hidden;
  background-color: $c-bg;
  color: $c-txt;
  font-variation-settings: 'wdth' 250, 'wght' 400;
  // 'wdth' 100—700, 'wght' 100—1000, 'slnt' -15—15
}

main {
  h1, h2, p {
    @include W(font-size, 0.25);
    line-height: 1em;
    text-transform: uppercase;
    margin: 0;

    &.big { @include W(font-size, 1); }

    .line {
      display: block;

      &.right { text-align: right; }
      &.center { text-align: center; }
    }
  }
}

.hello {
  @include grid(1, 6);
  @include H(font-size, 1);
}

