$cols: 8;

@mixin W($prop, $n, $plus: 0px) {
  #{$prop}: calc(100vw * #{$n} / #{$cols} + #{$plus});
}

@mixin H($prop, $n, $plus: 0px) {
  #{$prop}: calc(100vh * #{$n} / #{$cols} + #{$plus});
}

@mixin grid($start, $width, $rtl: false) {
  $dir: if(not $rtl, left, right);
  $nodir: if($rtl, left, right);
  position: absolute;
  @include W($dir, $start);
  @include W(width, $width);
  #{$nodir}: inherit;
}

$linear: cubic-bezier(0.25, 0.25, 0.75, 0.75) !default;
