export let mouse = { x: 0.5, y: 0.5, absX: 1, absY: 1 }
export let wheel = { x: 0, y: 0, deltaX: 0, deltaY: 0 }

window.addEventListener('mousemove', e => {
  mouse = {
    x: e.clientX / window.innerWidth,
    y: e.clientY / window.innerHeight,
    absX: e.clientX,
    absY: e.clientY
  }
})

window.addEventListener('wheel', e => {
  wheel = {
    x: wheel.x + e.deltaX,
    y: wheel.y + e.deltaY,
    deltaX: e.deltaX,
    deltaY: e.deltaY
  }
})

export function splitText (el) {
  const txt = el.textContent.split('')
  el.textContent = ''
  txt.forEach(letter => {
    const span = document.createElement('span')
    span.classList.add('lttr')
    span.textContent = letter
    el.append(span)
  })
  return el
}

export function lerp (start, end, amt) {
  return (1 - amt) * start + amt * end
}

export class FakeCursor {
  constructor (ease) {
    this.ease = ease
    this.pos = {
      x: 0,
      y: 0
    }

    this.el = this.createCursor()
    document.body.append(this.el)

    requestAnimationFrame(this.update.bind(this))
  }

  createCursor () {
    const cursor = document.createElement('i')
    cursor.classList.add('fake-cursor')
    Object.assign(cursor.style, {
      width: '10px',
      height: '10px',
      backgroundColor: 'currentColor',
      position: 'absolute',
      transform: 'rotate(45deg)',
      transition: 'transform .2s',
      left: (mouse.absX - 5) + 'px',
      top: (mouse.absY - 5) + 'px',
    })
    return cursor
  }

  update() {
    this.pos.x = lerp(this.pos.x, mouse.absX, this.ease)
    this.pos.y = lerp(this.pos.y, mouse.absY, this.ease)

    Object.assign(this.el.style, {
      left: (this.pos.x  - 5) + 'px',
      top: (this.pos.y - 5)  + 'px'
    })
    requestAnimationFrame(this.update.bind(this))
  }
}

export function map (num, in_min, in_max, out_min, out_max, limit = false) {
  const val = (num - in_min) * (out_max - out_min) / (in_max - in_min) + out_min;
  if (limit) {
    const limitMin = Math.min(out_max, out_min)
    const limitMax = Math.max(out_max, out_min)
    return Math.max(limitMin, Math.min(limitMax, val))
  }
  return val
}
