import * as Utils from '../utils'

const h1s = Array.from(document.querySelectorAll('h1.txt')).map(h1 => Utils.splitText(h1))
const texts = h1s.map(h1 => {
  return [...h1.querySelectorAll('span.lttr')].map(letterEl => ({
    el: letterEl,
    rect: letterEl.getBoundingClientRect(),
    wght: 200,
    wdth: 100,
    opct: 0,
  }))
})

requestAnimationFrame(update)

let lerpMouse = { x: Utils.mouse.x, y: Utils.mouse.y }

function update () {
  lerpMouse = {
    x: Utils.lerp(lerpMouse.x, Utils.mouse.x, 0.1),
    y: Utils.lerp(lerpMouse.y, Utils.mouse.y, 0.1)
  }

  texts.forEach(letters => {
    letters.forEach(letter => {
      letter.rect = letter.el.getBoundingClientRect()
      const dist = Math.hypot((letter.rect.x + letter.rect.width / 2) / window.innerWidth - lerpMouse.x, (letter.rect.y + letter.rect.height / 2) / window.innerHeight - lerpMouse.y)
      letter.wght = Utils.map(dist, 0, 0.2, 800, 200, true);
      letter.wdth = Utils.map(dist, 0, 0.4, 800, 100, true);
      letter.opct = Utils.map(dist, 0.1, 0.4, 1, 0, true);
      letter.el.style.fontVariationSettings = `'wght' ${letter.wght}, 'wdth' ${letter.wdth}`
      letter.el.style.opacity = letter.opct
    })
  })

  requestAnimationFrame(update)
}

document.body.style.cursor = 'none'
const mainCursor = new Utils.FakeCursor(0.4)
const backCursor = new Utils.FakeCursor(0.1)

document.querySelector('.text').addEventListener('mouseover', () => {
  mainCursor.el.style.transform = 'scale(2)'
  setTimeout(() =>{
    mainCursor.el.style.transform = 'rotate(45deg)'
  }, 300);
})

Object.assign(backCursor.el.style, {
  transform: 'none',
  backgroundColor: 'transparent',
  border: '1px solid currentColor',
  width: '8px',
  height: '8px'
})
