let mouse = { x: 0.5, y: 0.5 }
window.addEventListener('mousemove', e => {
  const i = 100
  mouse = {
    x: e.clientX / window.innerWidth,
    y: e.clientY / window.innerHeight
  }
  document.querySelector('.mouse').textContent = `${ Math.round(mouse.x * i)/i }—${ Math.round(mouse.y * i)/i }`
})

const h1 = splitText(document.querySelector('h1.txt'))
const letters = [...h1.querySelectorAll('span.lttr')].map(letterEl => ({
  el: letterEl,
  rect: letterEl.getBoundingClientRect(),
  wght: 0
}))

requestAnimationFrame(update)

function splitText (el) {
  const txt = el.textContent.split('')
  el.textContent = ''
  txt.forEach(letter => {
    const span = document.createElement('span')
    span.classList.add('lttr')
    span.textContent = letter
    el.append(span)
  })
  return el
}

function lerp (start, end, amt) {
  return (1 - amt) * start + amt * end
}

const a = 2000
let lerpMouse = { x: mouse.x, y: mouse.y }
function update () {
  lerpMouse = {
    x: lerp(lerpMouse.x, mouse.x, 0.1),
    y: lerp(lerpMouse.y, mouse.y, 0.1)
  }

  letters.forEach(letter => {
    letter.wght = -a + (1 - Math.hypot((letter.rect.x + letter.rect.width / 2) / window.innerWidth - lerpMouse.x, (letter.rect.y + letter.rect.height / 2) / window.innerHeight - lerpMouse.y)) * (800 + a);
    letter.el.style.fontVariationSettings = `'wght' ${letter.wght}`
    letter.el.style.opacity = letter.wght / 1000
  })

  document.querySelector('.wght').textContent = letters.map(l => Math.round(l.wght)).join(' | ')
  requestAnimationFrame(update)
}
